const forgetCMND = document.querySelector('#labelForget')
const listForm = document.querySelector('.listForm')
const btnNexts = document.querySelectorAll('.btn--next')
const btnPrevious = document.querySelectorAll('.btn--pre')
const overlay = document.querySelector('.overlay');
const turnOn = document.getElementById('turnOn')
const inputName = document.querySelector('input[name = "name"]');
const inputDate = document.querySelector('input[name = "date"]');
const inputQueQuan = document.querySelector('input[name = "queQuan"]');
const inputNoiThuongTru = document.querySelector('input[name = "noiThuongTru"]');
const date = new Date(2003, 06, 27);
const formQA = document.getElementById('formQA');
const preview = document.querySelector('.preview');
const btnLogout = document.getElementById('btnLogout');


btnLogout.onclick = () => {
    setTimeout(function(){
        location.replace("/Home/Home-Page/index.html");
      });
}
    function sleep(ms) {
        return new Promise(
            function(resolve) {
                setTimeout(resolve, ms);
            }
        )
    }
    const wrapper = document.querySelector('.wrapper');
    const container = document.querySelector('.form__wrapper--diffirent');
    const containerQA = document.querySelector('.container');
    const previousQA = document.getElementById('nextForm');
    console.log(previousQA);

    document.getElementById('turnOn').onclick = function(e){
        if (this.checked){
            overlay.style.display = 'block';
            containerQA.style.display = 'none';
        }
    };
    function beep() {
        var snd = new  Audio("data:audio/wav;base64,");  
        snd.play();
    }
        
    var scanner = new Instascan.Scanner({
        video: document.querySelector('.preview')
    });
    var check = false;
    var contents;
    function scannerQA() {
                scanner.addListener('scan', function(content) {
                    contents = content;
                    var arr = content.split("|");
                    if(content != undefined) {
                        sleep(0)
                        .then(() => {
                            beep();
                            return sleep(1500);
                        })
                        .then(() => {
                            overlay.style.display = 'none';
                            containerQA.style.display = 'block';
                            listForm.style.marginLeft = '-100%';
                            inputName.value = arr[0];
                            inputNoiThuongTru.value = arr[2];
                            inputQueQuan.value = arr[3];
                            inputDate.value = arr[1];
                            document.getElementById('male').checked = true;
                        })
                    }
                    else {
                        sleep(10000)
                            .then(() => {
                                overlay.style.display = 'block';
                                containerQA.style.display = 'none';
                                return sleep(0)
                            })
                            .then(() => {
                                overlay.style.display = 'none';
                                containerQA.style.display = 'block';
                            })
                    }
                });
            
                Instascan.Camera.getCameras().then(function (cameras) {
                    if (cameras.length > 0) {
                        scanner.start(cameras[0]);
                    }
                    else {
                        alert('No cameras found');
                    }
                }).catch(function(e){
                    alert(e);
                });
    }

    
    var i = 1, j = 1
    forgetCMND.onclick = () => {
        listForm.style.marginLeft = '-100%';
    }
    

    btnNexts.forEach(btnNext => {
        btnNext.onclick = () => {
            i++;
            if (i >= 3) {
                i = 2
            }
            listForm.style.marginLeft = '-' + i + '00%';
        }
    });
    console.log(btnPrevious);
    btnPrevious.forEach((btnPre, index) => {
        btnPre.onclick = () => {
            listForm.style.marginLeft = -index + '00%';
        }
    });


